// Download and save the PDF.js worker file
fetch('https://cdnjs.cloudflare.com/ajax/libs/pdf.js/4.0.379/pdf.worker.min.js')
  .then(response => response.text())
  .then(worker => {
    const blob = new Blob([worker], { type: 'application/javascript' });
    const workerUrl = URL.createObjectURL(blob);
    // Use the worker URL
    if (typeof window !== 'undefined') {
      window.pdfjsWorkerSrc = workerUrl;
    }
  });